changeSet.sql:

INSERT INTO m_policy (name, label, description) VALUES('Number of Recurring members according to policy', 'label.number.of.recurring.members', 'Cantidad de integrantes recurrentes según política');

UPDATE m_product_policy SET policy_id = (SELECT id FROM m_policy WHERE name = "Number of Recurring members according to policy" limit 1)
WHERE policy_id = (SELECT id FROM m_policy WHERE name = "Number of members according to policy" limit 1) AND product_id in(4,5);
