changeSet.sql:
DROP VIEW IF EXISTS custom.v_custom_charge_type_choices;
CREATE VIEW custom.v_custom_charge_type_choices AS SELECT avalMap.id as custom_charge_map_id, applicants.client_id, applicants.client_ally_id, applicants.custom_charge_type_id, term, percentage FROM custom.c_custom_charge_type_applicants applicants JOIN custom.c_custom_charge_type_map avalMap on avalMap.c_custom_charge_type_id = applicants.custom_charge_type_id WHERE applicants.active = true AND avalMap.active = true AND custom_charge_type_id IN ( SELECT id FROM custom.c_custom_charge_type ccct WHERE custom_charge_entity_id = ( SELECT id FROM custom.c_custom_charge_entity ccce WHERE name = 'Aval' ) AND ccct.name IN ('VIP','Commerce') )
UNION ALL
SELECT avalMap.id as custom_charge_map_id, null as client_id, null as client_ally_id, avalMap.c_custom_charge_type_id as custom_charge_type_id, term, percentage FROM custom.c_custom_charge_type_map avalMap WHERE avalMap.active = true AND avalMap.c_custom_charge_type_id IN ( SELECT id FROM custom.c_custom_charge_type ccct WHERE custom_charge_entity_id = ( SELECT id FROM custom.c_custom_charge_entity ccce WHERE name = 'Aval' ) AND ccct.name IN ('Product') ) -- ORDER BY -- custom_charge_type_id, term ;
